import React, {useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import io from "socket.io-client";

import Board from './Board';
import RightSide from "./RightSide";
import LeftSide from "./LeftSide";

import '../styles/home.css';

export default function Online({ socket }) {

    const { id } = useParams();

    const [color, setColor] = useState(null);
        const [buttonsTarget, setButtonsTarget] = useState(null);
    const [random, setRandom] = useState(null);

    socket.on("players", (player, socketId) => {
        console.log(player, socketId, id, socket.id, JSON.parse(localStorage.getItem("socket")));
        setColor(localStorage.getItem("white-player") != null ? localStorage.getItem("white-player") : player === 2 ? -1 : 1);
        if (player === 1 && localStorage.getItem("white-player") === null) localStorage.setItem("white-player", 1);
    });
   
    if (id != JSON.parse(localStorage.getItem("socket"))) return null;

    const returnButtonsClick = (e) => {

        let newTarget = e.target;

        while (!newTarget.classList.contains('button')) {
            newTarget = newTarget.parentElement;
        }

        setButtonsTarget(newTarget);
        setRandom(Math.random());
    };
    
    return color && (
        <div className="content">
            <div className="game">
                <LeftSide />
                <Board color={color} prevButtons={buttonsTarget} random={random}/>
                <RightSide clickButton={returnButtonsClick}/>
            </div>
        </div>
    );
};
