import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUsers, faUser, faRobot, faDesktop, faCheck } from '@fortawesome/free-solid-svg-icons';
import '../styles/game-modes.css';

import selectS from "../sounds/select.mp3";
import clickS from "../sounds/click.mp3";

export default function Home({id}) {

    console.log(id);
    localStorage.clear();

    const [stop, setStop] = useState(false);

    const inputLinkRef = useRef(null);
    const copyButtonRef = useRef(null);

    const selectSound = new Audio(selectS);
    const pointSound = new Audio(clickS);

    const hoverSound = () => {
        selectSound.pause();
        selectSound.currentTime = 0;
        selectSound.play();
    };

    const clickSound = (path) => {
        if (!stop) return;
        pointSound.pause();
        pointSound.currentTime = 0;
        pointSound.play();
        pointSound.addEventListener('ended', () => {
            if (path === `/${id}`) {
                localStorage.removeItem("board");
                localStorage.removeItem("current-move")
            }
            window.location.href = path;
        });
    
    };  

    const copyLink = (e) => {
        setStop(true);
        inputLinkRef?.current.select();
        inputLinkRef?.current.setSelectionRange(0, 1000);

        navigator.clipboard.writeText(inputLinkRef?.current.value);
        copyButtonRef?.current.classList.add("sent");

        setTimeout(() => {
            inputLinkRef?.current.blur();
            setStop(false);
        }, 150);
    };

    return (
       
        <div className="home-content">
             
            <div className="classic-mode game-mode" onMouseEnter={() => hoverSound()} onClick={() => clickSound("/classic")}>
                <div className="title">
                    <h2>Classic</h2>

                    <div className="icon">
                        <FontAwesomeIcon icon={faUser} 
                            color="#fff"
                            className="mode-icon"
                        />
                    </div>
                </div>
                
            </div>
           
            <div className="online-mode game-mode" onMouseEnter={() => hoverSound()} onClick={() => clickSound(`/${id}`)}>
                
                <div className="title">
                    <h2>Online</h2>

                    <div className="icon">
                        <FontAwesomeIcon 
                            icon={faUsers} 
                            color="#fff"
                            className="mode-icon"
                        />
                    </div>

                    
                </div>      
               
                <div className="link-container">
                    <p className="link-text">Link with your friend: </p>
                    <div className="link">
                        <label htmlFor="link-input">
                            <input type="text" className="link-input" value={`${window.location.href}${id}`} ref={inputLinkRef} onFocus={e => setStop(true)} onBlur={e => setStop(false)}/>
                        </label>

                        <div className="copy-button" onClick={(e) => copyLink(e)} ref={copyButtonRef}>
                            <FontAwesomeIcon 
                                icon={faCheck}
                                className="fa copy-icon"
                                
                            />
                        </div>
                    </div>
                    
                </div>

                <div className="icon">
                    
                </div>

                <div className="icon">
                    
                </div>
               
            </div>

            <div className="computer-mode game-mode" onMouseEnter={() => hoverSound()} onClick={() => clickSound("/computer")}>
                <div className="title">
                        <h2>Computer</h2>

                        <div className="icon">
                            <FontAwesomeIcon icon={faDesktop} 
                                color="#fff"
                                className="mode-icon"
                            />
                        </div>
                    </div>
                </div>
        </div>
    );
};
