import React, { useEffect, useState } from "react";
import Home from "./Components/Home";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import io from "socket.io-client";
import Classic from "./Components/Classic";
import Online from "./Components/Online";
import Computer from "./Components/Computer";

export default function App() {

    const [userSocket, setUserSocket] = useState(null);
    const [playerColor, setPlayerColor] = useState(localStorage.getItem("player-color") ? localStorage.getItem("player-color") : null);
    const [playerDifficulty, setPlayerDifficulty] = useState(localStorage.getItem("player-difficulty") ? localStorage.getItem("player-difficulty") : null);

    let id;

    useEffect(() => {
       
        const socket = io("http://localhost:8000");
        
        socket.on('connect', () => {
            
            console.log('connected', socket.id, socket);
            setUserSocket(socket);
            if (JSON.parse(localStorage.getItem("socket")) == null) localStorage.setItem("socket", JSON.stringify(socket.id));
            id = socket.id;
        });

        socket.on("ok", data => console.log(data));

    }, []);

    const getColor = (color) => {
        setPlayerColor(color);
        localStorage.setItem("player-color", color);
        
        // window.location.href = `${window.location.href}computer`;
    };

    const getDifficulty = (difficulty) => {
        setPlayerDifficulty(difficulty);
    };

    useEffect(() => {
        if (playerColor === null || playerDifficulty === null) return;
        return (
            <Router>
                <Switch>
    
                    <Route exact path="/">
                        <Home id={userSocket.id} getColor={getColor} getDifficulty={getDifficulty}/>
                    </Route>
    
                    <Route path="/classic">
                        <Classic />
                    </Route>
    
                    <Route path="/computer">
                        <Computer color={playerColor} difficulty={playerDifficulty} />
                    </Route>
    
                    <Route path={`/:id`}>
                        <Online socket={userSocket}/>
                    </Route>
            
                </Switch>
            </Router>
            
        );
        
    }, [playerColor, playerDifficulty]);

 
 
    return userSocket && userSocket.connected && userSocket.id !== undefined && (
        <Router>
            <Switch>

                <Route exact path="/">
                    <Home id={userSocket.id} getColor={getColor}/>
                </Route>

                <Route path="/classic">
                    <Classic />
                </Route>

                <Route path="/computer">
                    <Computer color={1} difficulty="easy"/>
                </Route>

                <Route path={`/:id`}>
                    <Online socket={userSocket}/>
                </Route>
        
            </Switch>
        </Router>
        
    );
  
};
