import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import '../styles/game-modes.css';

import selectS from "../sounds/select.mp3";
import clickS from "../sounds/click.mp3";

export default function Home() {

    const selectSound = new Audio(selectS);
    const pointSound = new Audio(clickS);

    const hoverSound = () => {
        selectSound.pause();
        selectSound.currentTime = 0;
        selectSound.play();
    };

    const clickSound = (path) => {
        pointSound.pause();
        pointSound.currentTime = 0;
        pointSound.play();
        pointSound.addEventListener('ended', () => {
            window.location.href = path;
        });
    
    };  

    return (
       
        <div className="home-content">
             
            <div className="classic-mode game-mode" onMouseEnter={() => hoverSound()} onClick={() => clickSound("/classic")}>
                <h2>Classic</h2>
            </div>
           
            <div className="online-mode game-mode" onMouseEnter={() => hoverSound()} onClick={() => clickSound("/online")}>
                <h2>Online</h2>
            </div>

            <div className="computer-mode game-mode" onMouseEnter={() => hoverSound()} onClick={() => clickSound("/computer")}>
                <h2>Computer</h2>
            </div>
        </div>
    );
};
